css:
.print-format .row:not(.section-break) {
    margin-top: 0px !important;
    padding: 0;
}
/*.col-xs-7.text-left.value {*/
/*    font-weight: normal;*/
/*}*/



.print-format .letter-head {
    margin-bottom: 0px !important;
}
.print-format {
    border-radius: 2px;
    border: 2px;
    padding: 2px !important;
    margin: 10px !important;
}

.table {
    margin: 10px 0px !important;
}
.table-sr {
    display: none;
}

.print-format th {
    color: black;
    font-weight: bold;
    padding: 2px !important;
}

.col-xs-5 label {
    font-weight: bold;
}

hr {
  margin-top: 5px !important;
  height: 12px !important;
  margin-bottom: 0px !important;
  border: 5px !important;
  border-top: 1px solid #000000 !important;
} 

.print-heading {
    border-bottom: 1px solid #000000;
    margin-bottom: 10px;
}

table.table-bordered {
    border-collapse: collapse; /* Ensures borders are clean */
    width: 100%;
}

table.table-bordered th,
table.table-bordered td {
    border: 0px solid black !important; /* Applies black border to all cells */
}

table.table-bordered thead th {
    border-bottom:0px solid black !important; /* Stronger border for header */
}

.col-xs-1, .col-xs-2, .col-xs-3, .col-xs-4, .col-xs-5, .col-xs-6, .col-xs-7, .col-xs-8, .col-xs-9, .col-xs-10, .col-xs-11, .col-xs-12 {
    padding-right: 3px !important;
    padding-left: 3px !important;
    margin: 0 !important;
}

.row {
    margin-right: 0 !important;
    margin-left: 0 !important;
}
.print-format td {
    padding: 4px !important;
}

.print-format table td .value {
    font-size: 13px !important;
}
