css:
.print-format .row:not(.section-break) {
    margin-top: 0px !important;
    padding: 0;
}
.col-xs-7.text-left.value {
    font-weight: normal; /* Ensure regular font-weight */
}



.print-format .letter-head {
    margin-bottom: 0px !important;
}
.print-format {
    border-radius: 2px;
    border: 2px;
    padding: 2px !important;
    margin: 5px !important;
}
.print-format td {
    padding: 2px !important;
}

.table {
    margin: 10px 0px !important;
}
.table-sr {
    text-align: center;
}

body {
    font-family: 'Roboto', sans-serif;
    font-size: 11px;
}

.print-format th {
    color: black;
    font-weight: bold;
    padding: 2px !important;
}

.print-format .row:not(.section-break) {
    margin-top: 5px !important;
}
.col-xs-5, .col-xs-6, .col-xs-7 {
    padding-right: 20px !important;
    padding-left: 20px !important;
}
/*.col-xs-7 {*/
/*    font-weight: bold !important;*/
/*}*/
.col-xs-3 {
    border-radius: 15px;
    border: 1px solid;
    text-align: center;
    padding-right: 5px !important;
    padding-left: 5px !important;
    width: 23% !important;
}

/*.col-xs-7 {*/
/*    position: relative;*/
/*    font-weight: bold;*/
/*}*/

.col-xs-5 label {
    width: 100%; /* Adjust the width as needed */
    font-weight: bold;
    color: black; 
    text-align: left;
}

hr {
  margin-top: 5px !important;
  height: 12px !important;
  margin-bottom: 0px !important;
  border: 5px !important;
  border-top: 1px solid #000000 !important;
} 

.col-xs-7.text-right.value {
    padding-right: -10px; 
    padding-left: -10px; 
}

