/*    margin-bottom: 10px;*/
/*}*/




table.table-bordered {
    border-collapse: collapse; /* Ensures borders are clean */
    width: 100%;
}

table.table-bordered th,
table.table-bordered td {
    border: 1px solid black !important; /* Applies black border to all cells */
}

table.table-bordered thead th {
    border-bottom: 1px solid black !important; /* Stronger border for header */
}
